  /**
   * Getter for the propiety `clave`.
   * @returns Returns the propiety `clave`.
   */
  getClave(): string {
    return this.clave;
  }
  /**
   * Sets a new `clave` if that new key belongs to the alphabet.
   * @param nuevaClave New value for `clave`.
   */
  setClave(nuevaClave: string): void {
    if (this.perteneceAlAlfabeto(nuevaClave)) {
      this.clave = nuevaClave;
    }
  }
  /**
   * Checks if a `string` belongs to the alphabet `alfabeto`.
   * @param str String to evaluate.
   * @returns Returns `true`if `str` belongs to the alphabet `alfabeto`.
   */
  perteneceAlAlfabeto(str: string): boolean {
    const caracteres: string[] = str.toLowerCase().split('');
    for (let i: number = 0; i < caracteres.length; i++) {
      if (this.alfabeto.toLowerCase().indexOf(caracteres[i]) === -1) return false;
    }
    return true;
  }

  /**
   * Encodes a message.
   * @param mensaje Message to encode.
   * @returns Returns the encoded message. If `mensaje` doesn't belong to `alfabeto`
   * it returns undefined.
   */
  codificar(mensaje: string): string | undefined {
    if (!this.perteneceAlAlfabeto(mensaje)) return undefined;
    let cifrado: string = '';
    let msgPos: number;
    let keyPos: number;
    for (let i: number = 0; i < mensaje.length; i++) {
      msgPos = this.alfabeto.toLowerCase().indexOf(mensaje[i].toLowerCase());
      keyPos = this.alfabeto.toLowerCase().indexOf(this.clave[i % this.clave.length].toLowerCase()) + 1;
      cifrado += this.alfabeto[(msgPos + keyPos) % this.alfabeto.length].toLowerCase();
    }
    return cifrado;
  }
  /**
   * Decodes a message.
   * @param mensaje Message to decode.
   * @returns Returns the decoded message. If `mensaje` doesn't belong to `alfabeto`
   * it returns undefined.
   */
  decodificar(mensaje: string): string | undefined {
    if (!this.perteneceAlAlfabeto(mensaje)) return undefined;
    let descifrado: string = '';
    let msgPos: number;
    let keyPos: number;
    let descifratedPos: number;
    for (let i: number = 0; i < mensaje.length; i++) {
      msgPos = this.alfabeto.indexOf(mensaje[i].toLowerCase());
      keyPos = this.alfabeto.indexOf(this.clave[i % this.clave.length].toLowerCase()) + 1;
      descifratedPos = msgPos - keyPos;
      if (descifratedPos < 0) descifratedPos += this.alfabeto.length;
      descifrado += this.alfabeto[descifratedPos].toLowerCase();
    }
    return descifrado;
  }
}

/**
 * Iterable class to represent prime numbers.
 */
export class PrimeNumber implements Iterable<number> {
  /**
   * Set of prime numbers.
   */
  private primes: Set<number>;
  /**
   * PrimeNumber unic instance.
   */
  private static primeNumberInstance: PrimeNumber;
  /**
   * Constructor for the class PrimeNumber.
   * @param primeNumbers Initial prime numbers array.
   */
  private constructor(primeNumbers: number[] = []) {
    this.primes = new Set(primeNumbers);
  }
  /**
   * Gets the unic PrimeNumber instance.
   * @returns Returns the unic PrimeNumber instance.
   */
  public static getPrimeNumberInstance(): PrimeNumber {
    if (!PrimeNumber.primeNumberInstance) {
      PrimeNumber.primeNumberInstance = new PrimeNumber();
    }
    return PrimeNumber.primeNumberInstance;
  }
  /**
   * Gets the first n prime numbers.
   * @param n Number of prime numbers to get.
   * @returns Returns an array with the first n prime numbers.
   */
  getFirstNPrimes(n: number): number[] {
    let count: number = 0;
    let primes: number[] = [];
    for (let i: number = 2; count != n; i++) {
      if (this.isPrime(i)) {
        primes.push(i);
        count++;
      }
    }
    primes.forEach((prime) => {
      PrimeNumber.primeNumberInstance.primes.add(prime);
    });
    return primes;
  }

  /**
   * Gets the prime numbers between n and m, these included.
   * @param n First number to look for prime numbers.
   * @param m Last number to look for prime numbers.
   * @returns Returns an array with the prime numbers between n and m, these included.
   */
  getPrimesBetween(n: number, m: number): number[] {
    let primes: number[] = [];
    for (let i: number = n; i <= m; i++) {
      if (this.isPrime(i)) {
        primes.push(i);
      }
    }
    primes.forEach((prime) => {
      PrimeNumber.primeNumberInstance.primes.add(prime);
    });
    return primes;
  }

  /**
   * Allows to iterate `primes`.
   * @returns Returns an Iterator.
   */
  [Symbol.iterator](): Iterator<number> {
    return PrimeNumber.primeNumberInstance.primes.values();
  }

  /**
   * Checks if a number is prime.
   * @param n Number to evaluate.
   * @returns Returns true if `n` is a prime number.
   */
  private isPrime(n: number): boolean {
    for (let i: number = 2; i*i <= n; i++) {
      if (n % i === 0) {
        return false;
      }
    }
    return true;
  }
}

